update.foreach:
update t_sys_pm_role_user
role_id = #{item.roleId,jdbcType=BIGINT}, user_id = #{item.userId,jdbcType=BIGINT}, state = #{item.state,jdbcType=SMALLINT}, create_user_id = #{item.createUserId,jdbcType=BIGINT}, create_user_name = #{item.createUserName,jdbcType=VARCHAR}, create_date = #{item.createDate,jdbcType=TIMESTAMP}, modify_user_id = #{item.modifyUserId,jdbcType=BIGINT}, modify_user_name = #{item.modifyUserName,jdbcType=VARCHAR}, modify_date = #{item.modifyDate,jdbcType=TIMESTAMP}
where role_user_id = #{item.roleUserId,jdbcType=BIGINT}

mapper.select:
select role_id
from t_sys_pm_role_user
where user_id = #{userId,jdbcType=BIGINT} and state=#{state,jdbcType=SMALLINT}
