mapper.select:
select 
from t_sys_pm_user
where state = 1 and user_id != 1

	and sys_type = 1


	and sys_type = 2 and proxy_id = #{proxyId,jdbcType=INTEGER}


	and sys_type = 3 and cust_id = #{custId,jdbcType=INTEGER}

update.foreach:
update t_sys_pm_user
login_name = #{item.loginName,jdbcType=VARCHAR}, pwd = #{item.pwd,jdbcType=VARCHAR}, user_name = #{item.userName,jdbcType=VARCHAR}, sys_type = #{item.sysType,jdbcType=INTEGER}, proxy_id = #{item.proxyId,jdbcType=INTEGER}, cust_id = #{item.custId,jdbcType=INTEGER}, is_manager = #{item.isManager,jdbcType=INTEGER}, email = #{item.email,jdbcType=VARCHAR}, mobile = #{item.mobile,jdbcType=VARCHAR}, state = #{item.state,jdbcType=SMALLINT}, create_user_id = #{item.createUserId,jdbcType=BIGINT}, create_user_name = #{item.createUserName,jdbcType=VARCHAR}, create_date = #{item.createDate,jdbcType=TIMESTAMP}, modify_user_id = #{item.modifyUserId,jdbcType=BIGINT}, modify_user_name = #{item.modifyUserName,jdbcType=VARCHAR}, modify_date = #{item.modifyDate,jdbcType=TIMESTAMP}
where user_id = #{item.userId,jdbcType=BIGINT}

mapper.select:
select

from t_sys_pm_user
where login_name = #{loginName,jdbcType=VARCHAR}
and pwd = #{pwd,jdbcType=VARCHAR}
and state >= 0

select user_id,user_name,COALESCE(depart_id, 0) depart_id
from public.t_sys_pm_user
where state = 1

	and sys_type = 1


	and sys_type = 2 and proxy_id = #{proxyId,jdbcType=INTEGER}


	and sys_type = 3 and cust_id = #{custId,jdbcType=INTEGER}

 	SELECT user_id, user_name FROM t_sys_pm_user
 	 where  state = 1 and user_name like '%'||#{name,jdbcType=VARCHAR}||'%'

		and sys_type = 1


		and sys_type = 2 and proxy_id = #{proxyId,jdbcType=INTEGER}


		and sys_type = 3 and cust_id = #{custId,jdbcType=INTEGER}

 	LIMIT 10
